"use strict";

module.exports = {
    validateFiles:function validateFiles(validationMetadata, fileResources) {
        var report = {issues: []};
        var errors;
        fileResources.forEach(function (fileResource) {
            var sFullPath = fileResource.getPath();
            errors = {
                    issues: [{
                        category: "Syntax Error",
                        checker: "",
                        helpUrl: "",
                        line: 1,
                        column: 1,
                        message: "dummy error",
                        source: "",
                        path: sFullPath
                    }]
                };
            report.issues = report.issues.concat(errors.issues);
        });
        return report;
    }
};
